from pylab import plot, show, savefig, xlim, figure, \
                hold, ylim, legend, boxplot, setp, axes

# function for setting the colors of the box plots pairs
def setBoxColors(bp):
    setp(bp['boxes'][0], color='blue', facecolor='white', linewidth=1.5)
    setp(bp['caps'][0], color='white')
    setp(bp['caps'][1], color='white')
    setp(bp['whiskers'][0], color='white')
    setp(bp['whiskers'][1], color='white')
    setp(bp['fliers'][0], color='white')
    setp(bp['fliers'][1], color='white')
    setp(bp['medians'][0], color='blue', linewidth=1.5)

    setp(bp['boxes'][1], color='red', facecolor='orange', linewidth=1.5)
    setp(bp['caps'][2], color='white')
    setp(bp['caps'][3], color='white')
    setp(bp['whiskers'][2], color='white')
    setp(bp['whiskers'][3], color='white')
    setp(bp['fliers'][2], color='white')
    setp(bp['fliers'][3], color='white')
    setp(bp['medians'][1], color='red', linewidth=1.5)

